sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
		return;
   }

	var reqTypeID = 'u_request_type';
	var reqTypeValue = g_form.getValue(reqTypeID);
	var whatNeededID = 'u_what_needed';

	g_form.clearOptions(whatNeededID);

	if(reqTypeValue == 'hr'){
		g_form.addOption(whatNeededID, 'hr1', 'Human Resources 1');
		g_form.addOption(whatNeededID, 'hr2', 'Human Resources 2');
	}

	if(reqTypeValue == 'facilities'){
		g_form.addOption(whatNeededID, 'facilities1', 'Facilities 1');
		g_form.addOption(whatNeededID, 'facilities2', 'Facilities 2');
	}

	if(reqTypeValue == 'legal'){
		g_form.addOption(whatNeededID, 'legal1', 'Legal 1');
		g_form.addOption(whatNeededID, 'legal2', 'Legal 2');
	}

	g_form.addOption(whatNeededID, 'other', 'Other');

}
